import { Navigate, Link } from "react-router-dom"
import { motion } from "framer-motion"
import { ToastContainer } from "react-toastify"
import BotaoADM from "../../Components/Adm/Button"
import SimpleBar from 'simplebar-react';
import 'simplebar/dist/simplebar.min.css';
import './index.scss'

export default function FinalPagamento(){

    const simpleBarStyle = {
        maxHeight: '30px', // Defina a altura máxima da área rolável
      };

      const thumbStyle = {
        backgroundColor: '#A83F37', // Cor de fundo vermelha para o thumb
        borderRadius: '8px', // Arredondamento desejado
        border: '2px solid #fff', // Borda branca de 2px
      };

    return (
        <main className="finalizacao">
            <SimpleBar style={simpleBarStyle}>
                <ToastContainer />

                <header className='header'>
                    <div className='sub-header-1'>
                        <Link to='/Feed'>
                            <motion.img src={'../../../../assets/images/Group 1.png'} className='logo-header-conf'
                                whileHover={{ scale: 1.1 }}
                                onHoverStart={e => { }}
                                onHoverEnd={e => { }}
                            />
                        </Link>
                    </div>
                    <div>

                    </div>
                    <p>Finalização do pagamento</p>
                </header>
                <div className="ass">
                    <h1>Pagamento finalizado com sucesso!</h1>
                    <Link to='/Feed'>
                    <BotaoADM nome='Continuar comprando'/>
                    </Link>
                </div>
                <div style={thumbStyle}></div>
            </SimpleBar>    
        </main>
    )


}
